//Para hacer pedidos de compras de articulos
<template>
  <div>
      <Header/>
      <b-container>
          <div class="shadow-sm p-3 mt-5 bg-white rounded buscar">
            <!--  -->
            <b-row align-h="between">
                <b-button v-on:click="getArray" size="sm" variant="transparent"><img src="@/assets/carrito.png" alt="buscar" class="imgCarrito" v-b-modal.carrito></b-button>
                <div inline class="former">
                    <b-input-group
                    size="sm"
                    >
                    <b-form-input id="articulobuscar" type="search" placeholder="articulo" class="inputart" v-on:keyup.enter="buscarA"></b-form-input>
                    <b-input-group-append>
                    <b-button size="sm" variant="transparent"><img src="@/assets/search.png" alt="buscar" class="imgbuscar" v-on:click="buscarA"></b-button>
                    </b-input-group-append>
                </b-input-group>
                </div>
                
            </b-row>

        </div>
        <!-- Tabla que muestra la informacion del articulo -->
        <div class="shadow-sm p-3 mt-3 bg-white rounded buscar" v-if="buscando">
            <table class="table table-hover table-sm table-striped table-reflow">
                <thead><th colspan="2" scope="col">Articulo</th></thead>
                <tbody>
                <tr><th style="width:25%" class="text-left" scope="row">Codigo</th>
                    <td class="text-left">{{DatosArticulo[0].COD_ARTICULO}}</td> </tr>
                <tr><th style="width:25%" class="text-left" scope="row">Descripcion</th>
                    <td class="text-left">{{DatosArticulo[0].DES_ARTICULO}}</td></tr>
                <tr><th style="width:25%" class="text-left" scope="row">Impuesto</th>
                    <td class="text-left">{{DatosArticulo[0].IMPUESTO}}</td></tr>
                <tr><th style="width:25%" class="text-left" scope="row">Precio</th>
                    <td class="text-left">{{DatosArticulo[0].PRECIO_BASE}}</td></tr>
                <tr><th style="width:25%" class="text-left" scope="row">Descuento</th>
                    <td class="text-left">{{DatosArticulo[0].DESC_PROM}}</td></tr>
                <tr><th style="width:25%" class="text-left" scope="row">Escala</th>
                    <td class="text-left">{{DatosArticulo[0].ESCALA}}</td></tr>
                <tr><th style="width:25%" class="text-left" scope="row">Bonifica</th>
                    <td class="text-left">{{DatosArticulo[0].BONIFICA}}</td></tr>
                    
                </tbody>
            
            
            </table>
            <b-row align-h="end">
                <!-- Si se escribe la cantidad y se da enter se agrega el articulo al carrito el articulo -->
                <b-form-input id="articuloCantidad" type="number" value="1" placeholder="U" min="1" class="inputnum pl-1" v-on:keyup.enter="agregarArticulo"></b-form-input>
                <!-- Este boton agrega un articulo -->
                <b-button size="sm" variant="primary" v-on:click="agregarArticulo" >Agregar</b-button>
            </b-row>
            <div>

            </div>
        </div>
      </b-container>
      
    <b-modal id="carrito" title="Lista de Compras" ref="carrito" hide-footer>
                  <div class="modal-body">
                      <!-- Este crea un card por cada articulo, en el cual muestra los datos de este articulo -->
                    <b-card border-variant="dark" class="cardtext" title="Articulo" v-for="(dato,index) in array" :key="dato.id">
                            <template #header>
                                <button type="button" class="close" v-on:click="eliminar(index)">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </template>
                        <b-card-text class="cardtext">Desc: {{dato.title}}</b-card-text>
                        <b-card-text class="cardtext">Precio U:{{dato.price}}</b-card-text>
                        <b-card-text class="cardtext">Cantidad:<input min="1" type="number" name="" v-model="dato.cantidad" v-on:change="changeTotal" class="inputnum pl-1 rounded"></b-card-text>
                        <b-card-text class="cardtext">Persona: <input type="text" name="" v-model="dato.nombre" class="inputname rounded"></b-card-text>
                    </b-card>

                  </div>
                  <div class="modal-footer">
                      <!-- Este muestra el total del articulo -->
                    <p class="mr-auto">Total :{{total.toLocaleString()}}</p>
                    <!-- Cierra el modal -->
                    <button type="button" class="btn btn-secondary" v-on:click="cancelModal">Cancelar</button>
                    <!-- Abre el modal para confirmar -->
                    <button type="button" class="btn btn-primary" v-b-modal.confirmar>Solicitar</button>
                    <!-- Se muestra si error es verdadero -->
                    <div class="alert alert-danger mr-auto" role="alert" v-if="error">
                        La compra no puede ser menor a 5000 colones
                    </div>
                  </div>
    </b-modal>
    <!-- Modal para confirmar -->
    <b-modal id="confirmar" title="Confirmar" ref="confirmar" hide-footer>
        <div class="modal-body">
            <span>Â¿Desea confirmar este pedido?</span>
        </div>
        <div class="modal-footer">
            <button type="button" class="btn btn-secondary" v-on:click="cancelPed">Cancelar</button>
            <!-- Hace que se realice la compra -->
            <button type="button" class="btn btn-primary" v-on:click="exportPDF">Confirmar</button>
        </div>
    </b-modal>
    <!-- Muestra el numero del pedido -->
    <b-modal id="pedido" title="Numero Pedido" ref="pedido" hide-footer>
        <div class="modal-body">
            <span>Numero de pedido: {{datoPedido}}</span>
        </div>
    </b-modal>
  </div>
</template>

<script>
import Header from '@/components/Header.vue';
import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
export default {
    name:'HacerPedido',
    components:{
        Header
    },
    data:function(){
        return{
            DatosArticulo:null,
            art:[],
            buscando:false,
            array:[],
            total:0,
            error:false,
            datoPedido:null,
            confirmar:false,
            
        }
    },
    mounted:function(){
        if(localStorage.getItem('articulo')==null){
            localStorage.setItem('articulo',JSON.stringify([]));
        }else{
            this.array=JSON.parse(localStorage.getItem('articulo'));
        }
        
    },
    updated:function(){
        localStorage.setItem('articulo',JSON.stringify(this.array));
    },
    methods:{
        actualizar:function(){
            localStorage.setItem('articulo',JSON.stringify(this.array));
        },
        eliminar:function(id){
            this.array.splice(id,1);
            localStorage.setItem('articulo',JSON.stringify(this.array));
            this.array=JSON.parse(localStorage.getItem('articulo'));
            this.changeTotal();
        },
        exportPDF() {
            if(this.total<5000){
                this.error=true;
            }else{
                var nombre=localStorage.nombrepersona+" "+localStorage.apellidos;
                var codigo=localStorage.cod_cliente;
                var vm = this
                var columns = [
                    {title: "Codigo Articulo", dataKey: "id"},
                    {title: "Descripcion", dataKey: "title"},
                    {title:"Cantidad",dataKey:'cantidad'},
                    // {title:"Nombre",dataKey:'nombre'}
                ];
                var imagedata="data:image/png;base64,";
                var doc = new jsPDF('p', 'pt');
                doc.setFontSize(16);
                doc.text('Lista de articulos', 40, 40);
                doc.setFontSize(12);
                doc.text(nombre,40,60);
                doc.text(codigo,40,80);
                doc.setFontSize(10);
                doc.text("Total: "+this.total+" colones",40,100);
                doc.setFontSize(9);
                //doc.text("Numero de pedido: "+this.datoPedido+"0",40,120),
                doc.addImage(imagedata,'PNG',460,30,100,40,'FAST');
                doc.autoTable(columns, vm.array, {
                    margin: {top: 100},
                    headStyles: {
                            fillColor: [113, 180, 240],
                            textColor: [255,255,255],
                            fontSize: 10,
                            padding: 0,
                    },
                    styles: {
                            fontSize: 8,
                    },
                });
                doc.save('ListaPDF.pdf');
                doc.autoPrint();
                doc.output("bloburl");
                doc.output(); 
                
                this.solicitar();
                
            }
        },
        solicitar:function(){
            var persona=localStorage.getItem('cod_cliente').substring(3,10);
            var to=this.total;
            if(persona.length <= 4){
                var y=new Date();
                var m=String(y.getMonth()+1).padStart(2,"0");
                var dd = String(y.getDate()).padStart(2, '0');
                var hh=String(y.getHours()).padStart(2, '0');
                var min=String(y.getMinutes()).padStart(2, '0');
                var ss = String(y.getSeconds()).padStart(2, '0');
                var str=persona+y.getFullYear().toString()+m+dd+hh+min+ss;

                let instance = axios.create();
                var rut=window.location.href;
                var ruta =rut.search("webapp/");
                var rutaFinal=rut.substring(0,ruta);
                let urlArticulos=rutaFinal+'webapi/api/compras/compra';
                var bearer= 'Bearer '+localStorage.token;

                let headers={
                    headers:{
                        "Content-Type": "Application/JSON",
                        "Authorization":bearer
                }
            }

                let json={
                    "user_id":  localStorage.getItem("nombre"),
                    "order_id":str,
                    "cod_cliente":localStorage.getItem("cod"),
                    "cart":this.array,
                    "total":String(to),
                    "envio":false,
                    "pickup":false,
                    "direccion":{
                        "Direccion":"",
                        "Ciudad":"",
                        "Provincia":"",
                        "Canton":"",
                        "Distrito":""
                    },
                    "facturar":false,
                    "datos_factura":null,
                    "vale":null
                };
                instance.post(urlArticulos,json,headers).then(data =>{
                    console.log(data);
                    this.datoPedido=data.data.message;
                }).catch((error)=>{
                    if(error.response){
                        if(error.response.status==401){
                            localStorage.clear();
                            this.$router.push('/webapp/');
                        }
                    }
                });
                //this.$refs['carrito'].hide();
                this.array=[];
                localStorage.setItem('articulo',JSON.stringify([]));
                this.$refs['carrito'].hide();
                this.$refs['confirmar'].hide();
                this.$refs['pedido'].show();

            }
        },
        changeTotal:function(){
            this.total=0;
            for(var i in this.array){
                var cant=parseFloat(this.array[i].cantidad);
                var pre=parseFloat(this.array[i].price);
                this.total=this.total+(cant*pre);
            }
        },
        getArray:function(){
            this.error=false;
            this.array=JSON.parse(localStorage.getItem('articulo'));
            this.changeTotal();
            axios.get('http://ipinfo.io').then(data=>{
                console.log(data)
            })
        },
        cancelModal:function(){
            this.$refs['carrito'].hide();
        },
        cancelPed:function(){
            this.$refs['confirmar'].hide();
        },
        agregarArticulo:function(){
            var cant=document.getElementById("articuloCantidad").value;
            var articulo={"id":this.DatosArticulo[0].COD_ARTICULO,"title":this.DatosArticulo[0].DES_ARTICULO,"price":String(this.DatosArticulo[0].PRECIO_BASE),"cantidad":String(cant)/* ,nombre:localStorage.getItem('nombrepersona') */};
            // if(this.art == null && ){
            //     this.art=JSON.parse(localStorage.getItem('articulo'));
            // }else{
            //     this.art.push(articulo);

            // }
            this.art=JSON.parse(localStorage.getItem('articulo'));

            this.art.push(articulo);
            
           // Storage.setObj('user.compras.'+this.DatosArticulo[0].COD_ARTICULO,articulo);
            localStorage.setItem('articulo',JSON.stringify(this.art));
            this.array=JSON.parse(localStorage.getItem('articulo'));
            document.getElementById('articuloCantidad').value='1';
        },
        buscarA:function(){
            this.articulo=document.getElementById('articulobuscar').value;
            this.articulo.toUpperCase();
            let instance = axios.create();
            var rut=window.location.href;
            var ruta =rut.search("webapp/");
            var rutaFinal=rut.substring(0,ruta);
            let urlArticulos=rutaFinal+'/webapi/api/productos/Producto?pProducto='+this.articulo;
            var bearer= 'Bearer '+localStorage.token;

            let headers={
                headers:{
                    "Content-Type": "Application/JSON",
                    "Authorization":bearer
            }
            }

            let json={
                
            };
            instance.post(urlArticulos,json,headers).then(data =>{
                this.DatosArticulo=data.data.Productos;
            }).catch((error)=>{
                if(error.response){
                    if(error.response.status==401){
                        localStorage.clear();
                        this.$router.push('/webapp/');
                    }
                }
            });
            this.buscando=true;
        }
    }
}
</script>

